import React, { Component } from 'react';
import ProfilePic from '../Assets/ProfilePic.png';
import { Grid, Cell } from 'react-mdl';
import Education from './education';
import Experience from './experience';
import Skills from './skills';

class Resume extends Component {
    render() {
        return (

            <div>
            <Grid>
              <Cell col={4}>
                <div style={{textAlign: 'center'}}>
                  <img
                    src={ProfilePic}
                    alt="avatar"
                    style={{height: '200px'}}
                     />
                </div>
    
                <h2 style={{paddingTop: '2em',color:'#b92b27'}}>Karan Kr. Gupta</h2>
                <h4 style={{color: 'grey'}}>Programmer</h4>
                <hr style={{borderTop: '3px solid #833fb2', width: '50%'}}/>
                <p>Though I have no experience ,yet proficient in HTML/CSS, JavaScript,Bootstrap and ReactJs & Flutter; plus modern libraries and frameworks.Building websites and apps that are responsive and usable.Identifying innovative ideas and proof of concepts according to project requirements.</p>
                <hr style={{borderTop: '3px solid #833fb2', width: '50%'}}/>
                <h5 style={{color:'grey',fontWeight:'bold'}}>Address</h5>
                <p>1 Hacker Way Menlo Park, 94025</p>
                <h5 style={{color:'grey',fontWeight:'bold'}}>Phone</h5>
                <p>(123) 456-7890</p>
                <h5 style={{color:'grey',fontWeight:'bold'}}>Email</h5>
                <p>someone@example.com</p>
                <h5 style={{color:'grey',fontWeight:'bold'}}>Web</h5>
                <p>mywebsite.com</p>
                <hr style={{borderTop: '3px solid #833fb2', width: '50%'}}/>
              </Cell>
              <Cell className="resume-right-col" col={8}>
                <h2 style={{color:'grey',fontWeight:'bold'}}>Education</h2>
    
    
                <Education
                  startYear={2016}
                  endYear={2020}
                  schoolName="ABES Institute Of Technology"
                  schoolDescription="Pursued my BTech in Computer Science branch from here."
                   />
    
                   <Education
                     startYear={2007}
                     endYear={2009}
                     schoolName="My 2nd University"
                     schoolDescription="Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s"
                      />
                    <hr style={{borderTop: '3px solid #e22947'}} />
    
                  <h2 style={{color:'grey',fontWeight:'bold'}}>Experience</h2>
    
                <Experience
                  startYear={2009}
                  endYear={2012}
                  jobName="First Job"
                  jobDescription="Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s"
                  />
    
                  <Experience
                    startYear={2012}
                    endYear={2016}
                    jobName="Second Job"
                    jobDescription="Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s"
                    />
                  <hr style={{borderTop: '3px solid #e22947'}} />
                  <h2 style={{color:'grey',fontWeight:'bold'}}>Skills</h2>
                  <Skills
                    skill="javascript"
                    progress={100}
                    />
                    <Skills
                      skill="HTML/CSS"
                      progress={80}
                      />
                      <Skills
                        skill="NodeJS"
                        progress={50}
                        />
                        <Skills
                          skill="React"
                          progress={25}
                          />
    
    
              </Cell>
            </Grid>
          </div>
    

        );

    }
}

export default Resume;
