import React, { Component } from 'react';
import { Tabs, Tab } from 'react-mdl';
import "../App.css";

class Projects extends Component {
    constructor(props) {
        super(props)
        this.state = { activeTab: 0 };
    }

    toggleCategories() {

        if (this.state.activeTab === 0) {

            return (
                <div className="projects-grid"><h1>This is ReactJs</h1></div>
            )
        }
        else if (this.state.activeTab === 1) {

            return (
                <div className="projects-grid"><h1>This is Bootstrap</h1></div>
            )
        }
        else if (this.state.activeTab === 2) {

            return (
                <div className="projects-grid"><h1>This is Javascript</h1></div>
            )
        }
        else if (this.state.activeTab === 3) {
                return (
                    <div className="projects-grid"><h1>This is HTML/CSS</h1></div>
                )
            }
        }


        render() {
            return (

                <div className="category-tabs">
                    <Tabs activeTab={this.state.activeTab} onChange={(tabId) => this.setState({ activeTab: tabId })} ripple>
                        <Tab>React</Tab>
                        <Tab>Bootstrap</Tab>
                        <Tab>Javascript</Tab>
                        <Tab>HTML/CSS</Tab>
                    </Tabs>
                    <section className="projects-grid">
                        {this.togglecategories}
                    </section>
                </div>

            );

        }
    }

    export default Projects;
